import React, { useState, useEffect } from 'react';
import axios from 'axios';

function MDTList() {
    const [mdts, setMdts] = useState([]); // State to hold MDT data

    // Fetch data from the backend
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/load-mdt-data')
            .then(response => {
                console.log("Fetched data:", response.data); // Log fetched data
                setMdts(response.data); // Update state with fetched data
            })
            .catch(error => {
                console.error('Error fetching MDT data:', error);
            });
    }, []);

    // Function to send the current table data to the backend
    const sendToReturnedTab = () => {
        axios.post('http://127.0.0.1:5000/update-returned-tab', mdts)
            .then(response => {
                alert(response.data.message); // Show success message
            })
            .catch(error => {
                console.error('Error updating Returned tab:', error);
            });
    };

    return (
        <div>
            <h2>MDT Inventory</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Unit</th>
                        <th>Star #</th>
                        <th>PC Serial</th>
                        <th>PC Model</th>
                        <th>Aircard IP</th>
                        <th>PC Name</th>
                        <th>Term ID</th>
                    </tr>
                </thead>
                <tbody>
                    {mdts.map((mdt, index) => (
                        <tr key={index}>
                            <td>{mdt.NAME}</td>
                            <td>{mdt.UNIT}</td>
                            <td>{mdt["STAR #"]}</td>
                            <td>{mdt.pc_serial}</td>
                            <td>{mdt.pc_model}</td>
                            <td>{mdt.aircard_ip}</td>
                            <td>{mdt.pc_name}</td>
                            <td>{mdt.term_id}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {/* Add a button to send data to the backend */}
            <button onClick={sendToReturnedTab}>Update Returned Tab</button>
        </div>
    );
}

export default MDTList;
